using System.Collections;using System.Collections.Generic;using UnityEngine;public class Interactable : MonoBehaviour {    List<IInteractionBehavior> behaviors;    void Start()    {        behaviors = new List<IInteractionBehavior>();        foreach (Component c in GetComponents<Component>())        {            if (c is IInteractionBehavior)            {                behaviors.Add(c as IInteractionBehavior);            }        }    }	public void OnMouseDown()    {        foreach (IInteractionBehavior  c in behaviors)        {            c.PerformBehavior();        }    }}